package com.shaya.happid.ui.activity

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import com.shaya.happid.databinding.ActivitySplashBinding
import com.shaya.happid.utils.SPLASH_WAIT_DURATION
import com.shaya.happid.utils.loadCircleImageByUrl


class SplashActivity : AppCompatActivity() {

    private lateinit var binding : ActivitySplashBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivitySplashBinding.inflate(layoutInflater)
        setContentView(binding.root)
        loadOnEntryImages()
        startLandingActivity()
    }

    private fun startLandingActivity() {
        Handler(Looper.getMainLooper()).postDelayed(
            {
                val intent = Intent(this, LandingActivity::class.java)
                startActivity(intent)
                finish()
            }, SPLASH_WAIT_DURATION
        )
    }

    private fun loadOnEntryImages() {
       binding.apply {
           imgOne.loadCircleImageByUrl(this@SplashActivity, url = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJIzlyH7l2Nvt6qh1fcOAlvjr0ey3lBq7ymQ&usqp=CAU")
           imgTwo.loadCircleImageByUrl(this@SplashActivity, url = "https://t4.ftcdn.net/jpg/01/68/63/23/360_F_168632335_25GIq7okU1EwpQEiQzbrRuE69YuPECy6.jpg")
           imgThree.loadCircleImageByUrl(this@SplashActivity, url = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_oO_96PBHQQpjQVRqQFzTQNX4YNFI3u3M3A&usqp=CAU")
           imgFour.loadCircleImageByUrl(this@SplashActivity, url = "https://cellularnews.com/wp-content/uploads/2020/03/04-waterfall-scenery-wallpaper-325x485.jpg")
           imgFive.loadCircleImageByUrl(this@SplashActivity, url = "https://www.xtrafondos.com/wallpapers/vertical/paisaje-de-lago-en-las-montanas-al-amanecer-7254.jpg")
           imgSix.loadCircleImageByUrl(this@SplashActivity, url = "https://cdn.pixabay.com/photo/2013/07/18/10/56/railroad-163518__340.jpg")
           imgSeven.loadCircleImageByUrl(this@SplashActivity, url = "data:image/jpeg;base64,")
       }
    }
}